import { getVideoDetailApi_2 } from '@/request';
import { opIdToVal, VideoDetail, videoEpOp } from '@/type';
import { getSearchParams } from '@/utils';
import { Row, Col, Space, Image, Card, Typography, Button } from 'antd';
import { useEffect, useState } from 'react';
import { useLocation, useNavigate, useSearchParams } from 'react-router-dom';

const { Title } = Typography;

export const VideoContentDetail = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const state = location.state;
    const [searchParams, setSearchParams] = useSearchParams();
    const obj = getSearchParams(searchParams);
    const [videoData, setVideoData] = useState<VideoDetail>(null);

    const getData = () => {
        getVideoDetailApi_2(obj.id).then((res: any) => {
            setVideoData(res.data);
        });
    }

    const onBack = () => {
        navigate('/Video2/ContentMgr/ContentList', { state: state, replace: false });
    }

    useEffect(() => {
        getData();
    }, [])

    return (
        <Row gutter={[12, 30]}>
            <Col span={24}>
                <Title level={2} style={{ margin: 0 }}>视频详情</Title>
            </Col>
            <Col span={24}>
                <Button type="primary" onClick={onBack}>返回</Button>
            </Col>
            <Col span={24}>
                <Card>
                    <Space align="start" size={[30, 10]}>
                        <Image
                            width={200}
                            // height={300}
                            src={videoData?.pic}
                            fallback="data:image/png;base64," />
                        <Space direction="vertical" style={{ width: '100%' }}>
                            <Title level={5}>基础信息-------</Title>
                            {`视频名称：${videoData?.name || ''}`}
                            <Space style={{ width: '100%' }} align="start" size={[0, 10]}>
                                <div style={{ whiteSpace: 'nowrap' }}>视频简介：</div>
                                <div>{videoData?.introduction || ''}</div>
                            </Space>
                            <Space style={{ width: '100%' }} align="start" size={[0, 10]}>
                                <div style={{ whiteSpace: 'nowrap' }}>视频介绍：</div>
                                <div>{videoData?.present || ''}</div>
                            </Space>
                            {`视频主演：${(videoData && videoData.starring) ? videoData.starring.map((v) => v.name).join('、') : ''}`}
                            {`视频导演：${(videoData && videoData.director) ? videoData.director.map((v) => v.name).join('、') : ''}`}
                            {`视频分类：${videoData?.cid_text || ''}`}
                            {`视频类型：${videoData?.type || ''}`}
                            {`地区：${videoData?.area || ''}`}
                            {`年代：${videoData?.year || ''}`}
                            {`视频状态：${videoData?.video_mode_text || ''}`}
                            {`搜索关键字：${videoData?.tags || ''}`}
                            {`基础评分：${videoData?.basic_score || ''}`}
                            {`综合评分：${videoData?.score || ''}`}
                            {`总数：${videoData?.total || ''}${(videoData && videoData.section) ? opIdToVal(videoEpOp, videoData?.section) : ''}`}
                            <Title level={5}>其他信息-------</Title>
                            {`创建人：${videoData?.user_name || ''}`}
                            {`创建时间：${videoData?.created_at || ''}`}
                            {`开关状态：${videoData?.state == '1' ? '开' : '关'}`}
                        </Space>
                    </Space>
                </Card>
            </Col>
        </Row>
    )
}
